  --shadow-sm: 0px 2px 4px -1px hsl(240 10% 2% / 0.25), 0px 1px 2px -1px hsl(240 10% 2% / 0.20);
  --shadow: 0px 4px 6px -1px hsl(240 10% 2% / 0.30), 0px 2px 4px -2px hsl(240 10% 2% / 0.25);
  --shadow-md: 0px 6px 12px -2px hsl(240 10% 2% / 0.35), 0px 3px 6px -3px hsl(240 10% 2% / 0.30);
  --shadow-lg: 0px 10px 20px -4px hsl(240 10% 2% / 0.40), 0px 4px 8px -4px hsl(240 10% 2% / 0.35);
  --shadow-xl: 0px 20px 30px -6px hsl(240 10% 2% / 0.45), 0px 8px 12px -6px hsl(240 10% 2% / 0.40);
