import { useAuth } from "@/hooks/useAuth";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Skeleton } from "@/components/ui/skeleton";
import { Send, MessageSquare } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { User, Conversation, Message } from "@shared/schema";
import { formatDistanceToNow } from "date-fns";
import { AppHeader } from "@/components/AppHeader";

type ConversationWithUser = Conversation & {
  otherUser: User;
};

export default function Messages() {
  const { user } = useAuth();
  const [selectedConversation, setSelectedConversation] = useState<string | null>(null);
  const [messageContent, setMessageContent] = useState("");

  // Check URL params for conversation ID
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const conversationId = params.get("conversation");
    if (conversationId) {
      setSelectedConversation(conversationId);
    }
  }, []);

  const { data: conversations, isLoading: conversationsLoading } = useQuery<ConversationWithUser[]>({
    queryKey: ["/api/conversations"],
  });

  const { data: messages, isLoading: messagesLoading } = useQuery<Message[]>({
    queryKey: ["/api/messages", selectedConversation],
    enabled: !!selectedConversation,
  });

  const sendMessageMutation = useMutation({
    mutationFn: async (data: { conversationId: string; content: string }) => {
      return await apiRequest("POST", "/api/messages", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/messages", selectedConversation] });
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      setMessageContent("");
    },
  });

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!messageContent.trim() || !selectedConversation) return;

    sendMessageMutation.mutate({
      conversationId: selectedConversation,
      content: messageContent,
    });
  };

  const getUserInitials = (u: User | undefined) => {
    if (!u) return "U";
    if (u.firstName && u.lastName) {
      return `${u.firstName[0]}${u.lastName[0]}`;
    }
    return u.email?.[0]?.toUpperCase() || "U";
  };

  const selectedConv = conversations?.find(c => c.id === selectedConversation);

  return (
    <div className="min-h-screen bg-background flex flex-col">
      <AppHeader />
      <div className="flex flex-1 overflow-hidden">
        <div className="w-80 border-r">
        <div className="p-4 border-b">
          <h2 className="text-lg font-semibold">Messages</h2>
        </div>
        <ScrollArea className="h-[calc(100vh-8rem)]">
          {conversationsLoading ? (
            <div className="p-4 space-y-3">
              {[1, 2, 3].map(i => (
                <div key={i} className="flex items-center gap-3">
                  <Skeleton className="h-12 w-12 rounded-full" />
                  <div className="flex-1 space-y-2">
                    <Skeleton className="h-4 w-32" />
                    <Skeleton className="h-3 w-48" />
                  </div>
                </div>
              ))}
            </div>
          ) : conversations && conversations.length > 0 ? (
            <div className="p-2">
              {conversations.map((conv) => (
                <button
                  key={conv.id}
                  onClick={() => setSelectedConversation(conv.id)}
                  className={`w-full p-3 flex items-center gap-3 rounded-lg hover-elevate active-elevate-2 text-left ${
                    selectedConversation === conv.id ? "bg-accent" : ""
                  }`}
                  data-testid={`conversation-${conv.id}`}
                >
                  <Avatar className="h-12 w-12">
                    <AvatarImage src={conv.otherUser.profileImageUrl || undefined} />
                    <AvatarFallback>{getUserInitials(conv.otherUser)}</AvatarFallback>
                  </Avatar>
                  <div className="flex-1 min-w-0">
                    <p className="font-medium truncate">
                      {conv.otherUser.firstName} {conv.otherUser.lastName}
                    </p>
                    <p className="text-sm text-muted-foreground truncate">
                      {conv.lastMessageAt ? formatDistanceToNow(new Date(conv.lastMessageAt), { addSuffix: true }) : "No messages"}
                    </p>
                  </div>
                </button>
              ))}
            </div>
          ) : (
            <div className="flex flex-col items-center justify-center h-64 text-center px-4">
              <MessageSquare className="h-12 w-12 text-muted-foreground mb-3" />
              <p className="text-muted-foreground">No conversations yet</p>
              <p className="text-sm text-muted-foreground mt-1">
                Start a conversation from a user profile
              </p>
            </div>
          )}
        </ScrollArea>
        </div>

        <div className="flex-1 flex flex-col">
        {selectedConv ? (
          <>
            <div className="p-4 border-b flex items-center gap-3">
              <Avatar className="h-10 w-10">
                <AvatarImage src={selectedConv.otherUser.profileImageUrl || undefined} />
                <AvatarFallback>{getUserInitials(selectedConv.otherUser)}</AvatarFallback>
              </Avatar>
              <div>
                <p className="font-medium">
                  {selectedConv.otherUser.firstName} {selectedConv.otherUser.lastName}
                </p>
                <p className="text-sm text-muted-foreground">{selectedConv.otherUser.email}</p>
              </div>
            </div>

            <ScrollArea className="flex-1 p-4">
              {messagesLoading ? (
                <div className="space-y-4">
                  {[1, 2, 3].map(i => (
                    <div key={i} className="flex gap-3">
                      <Skeleton className="h-8 w-8 rounded-full" />
                      <Skeleton className="h-16 w-64" />
                    </div>
                  ))}
                </div>
              ) : messages && messages.length > 0 ? (
                <div className="space-y-4">
                  {messages.map((message) => {
                    const isOwn = message.senderId === user?.id;
                    return (
                      <div
                        key={message.id}
                        className={`flex gap-3 ${isOwn ? "flex-row-reverse" : ""}`}
                        data-testid={`message-${message.id}`}
                      >
                        <Avatar className="h-8 w-8">
                          <AvatarImage
                            src={isOwn ? user?.profileImageUrl || undefined : selectedConv.otherUser.profileImageUrl || undefined}
                          />
                          <AvatarFallback>
                            {isOwn ? getUserInitials(user) : getUserInitials(selectedConv.otherUser)}
                          </AvatarFallback>
                        </Avatar>
                        <div className={`flex flex-col gap-1 max-w-md ${isOwn ? "items-end" : ""}`}>
                          <Card className={`p-3 ${isOwn ? "bg-primary text-primary-foreground" : ""}`}>
                            <p className="text-sm whitespace-pre-wrap">{message.content}</p>
                          </Card>
                          <p className="text-xs text-muted-foreground">
                            {formatDistanceToNow(new Date(message.createdAt!), { addSuffix: true })}
                          </p>
                        </div>
                      </div>
                    );
                  })}
                </div>
              ) : (
                <div className="flex flex-col items-center justify-center h-full text-center">
                  <MessageSquare className="h-16 w-16 text-muted-foreground mb-3" />
                  <p className="text-muted-foreground">No messages yet</p>
                  <p className="text-sm text-muted-foreground mt-1">Send a message to start the conversation</p>
                </div>
              )}
            </ScrollArea>

            <form onSubmit={handleSendMessage} className="p-4 border-t">
              <div className="flex gap-2">
                <Input
                  value={messageContent}
                  onChange={(e) => setMessageContent(e.target.value)}
                  placeholder="Type a message..."
                  className="flex-1"
                  data-testid="input-message"
                />
                <Button
                  type="submit"
                  size="icon"
                  disabled={!messageContent.trim() || sendMessageMutation.isPending}
                  data-testid="button-send-message"
                >
                  <Send className="h-4 w-4" />
                </Button>
              </div>
            </form>
          </>
        ) : (
          <div className="flex flex-col items-center justify-center h-full text-center px-4">
            <MessageSquare className="h-20 w-20 text-muted-foreground mb-4" />
            <h3 className="text-lg font-semibold mb-2">Select a conversation</h3>
            <p className="text-muted-foreground">
              Choose a conversation from the left to start messaging
            </p>
          </div>
        )}
        </div>
      </div>
    </div>
  );
}

client/src/components/NotificationBell.t.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { Bell, Check, CheckCheck, CheckCircle2, XCircle, FileText, MessageSquare, Target } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { formatDistanceToNow } from "date-fns";
import type { Notification } from "@shared/schema";
import { useState } from "react";

export function NotificationBell() {
  const [open, setOpen] = useState(false);

  const { data: notifications = [], isLoading } = useQuery<Notification[]>({
    queryKey: ["/api/notifications"],
  });

  const { data: unreadData } = useQuery<{ count: number }>({
    queryKey: ["/api/notifications/unread-count"],
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  const markAsReadMutation = useMutation({
    mutationFn: async (notificationId: string) => {
      return await apiRequest("PATCH", `/api/notifications/${notificationId}/read`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/notifications/unread-count"] });
    },
  });

  const markAllReadMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("POST", "/api/notifications/mark-all-read", {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/notifications/unread-count"] });
    },
  });

  const unreadCount = unreadData?.count || 0;

  const getNotificationIcon = (type: string) => {
    const iconClass = "h-5 w-5";
    switch (type) {
      case 'application_accepted':
        return <CheckCircle2 className={`${iconClass} text-green-500`} />;
      case 'application_rejected':
        return <XCircle className={`${iconClass} text-red-500`} />;
      case 'application_submitted':
        return <FileText className={`${iconClass} text-blue-500`} />;
      case 'new_message':
        return <MessageSquare className={`${iconClass} text-purple-500`} />;
      case 'new_opportunity':
        return <Target className={`${iconClass} text-orange-500`} />;
      default:
        return <Bell className={`${iconClass} text-muted-foreground`} />;
    }
  };

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="ghost"
          size="icon"
          className="relative"
          data-testid="button-notifications"
        >
          <Bell className="h-5 w-5" />
          {unreadCount > 0 && (
            <Badge
              variant="destructive"
              className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center p-0 text-xs"
            >
              {unreadCount > 9 ? '9+' : unreadCount}
            </Badge>
          )}
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-80 p-0" align="end">
        <div className="flex items-center justify-between p-4 border-b">
          <h3 className="font-semibold">Notifications</h3>
          {unreadCount > 0 && (
            <Button
              variant="ghost"
              size="sm"
              onClick={() => markAllReadMutation.mutate()}
              disabled={markAllReadMutation.isPending}
              data-testid="button-mark-all-read"
            >
              <CheckCheck className="h-4 w-4 mr-1" />
              Mark all read
            </Button>
          )}
        </div>
        <ScrollArea className="h-96">
          {isLoading ? (
            <div className="p-4 text-center text-sm text-muted-foreground">
              Loading notifications...
            </div>
          ) : notifications.length === 0 ? (
            <div className="p-8 text-center">
              <Bell className="h-12 w-12 mx-auto mb-3 text-muted-foreground" />
              <p className="text-sm text-muted-foreground">No notifications yet</p>
            </div>
          ) : (
            <div className="divide-y">
              {notifications.map((notification) => (
                <div
                  key={notification.id}
                  className={`p-4 hover-elevate cursor-pointer ${
                    notification.read === 0 ? "bg-accent/50" : ""
                  }`}
                  onClick={() => {
                    if (notification.read === 0) {
                      markAsReadMutation.mutate(notification.id);
                    }
                  }}
                  data-testid={`notification-${notification.id}`}
                >
                  <div className="flex gap-3">
                    <div className="flex-shrink-0 mt-1">
                      {getNotificationIcon(notification.type)}
                    </div>
                    <div className="flex-1 min-w-0">
                      <div className="flex items-start justify-between gap-2">
                        <p className="font-medium text-sm">{notification.title}</p>
                        {notification.read === 0 && (
                          <div className="h-2 w-2 rounded-full bg-primary flex-shrink-0 mt-1" />
                        )}
                      </div>
                      <p className="text-sm text-muted-foreground mt-1">
                        {notification.message}
                      </p>
                      <p className="text-xs text-muted-foreground mt-2">
                        {formatDistanceToNow(new Date(notification.createdAt!), {
                          addSuffix: true,
                        })}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </ScrollArea>
      </PopoverContent>
    </Popover>
  );
}

client/src/pages/settings.tsx.content:
import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { ArrowLeft, User, Bell, Lock, LogIn, LogOut } from "lucide-react";
import { SimpleUploader } from "@/components/SimpleUploader";
import { isUnauthorizedError } from "@/lib/authUtils";
import type { UserPreferences } from "@shared/schema";

export default function Settings() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [profileImageUrl, setProfileImageUrl] = useState("");

  const { data: preferences } = useQuery<UserPreferences>({
    queryKey: ["/api/preferences"],
  });

  const [emailNotifications, setEmailNotifications] = useState(true);
  const [applicationNotifications, setApplicationNotifications] = useState(true);
  const [messageNotifications, setMessageNotifications] = useState(true);
  const [opportunityNotifications, setOpportunityNotifications] = useState(true);
  const [profileVisibility, setProfileVisibility] = useState<string>("public");

  useEffect(() => {
    if (user) {
      setFirstName(user.firstName || "");
      setLastName(user.lastName || "");
      setEmail(user.email || "");
      setProfileImageUrl(user.profileImageUrl || "");
    }
  }, [user]);

  useEffect(() => {
    if (preferences) {
      setEmailNotifications(preferences.emailNotifications === 1);
      setApplicationNotifications(preferences.applicationNotifications === 1);
      setMessageNotifications(preferences.messageNotifications === 1);
      setOpportunityNotifications(preferences.opportunityNotifications === 1);
      setProfileVisibility(preferences.profileVisibility || "public");
    }
  }, [preferences]);

  const updateUserMutation = useMutation({
    mutationFn: async (data: { firstName: string; lastName: string }) => {
      return await apiRequest("PATCH", "/api/auth/user", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Success",
        description: "Account updated successfully",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updatePreferencesMutation = useMutation({
    mutationFn: async (data: Partial<UserPreferences>) => {
      return await apiRequest("POST", "/api/preferences", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/preferences"] });
      toast({
        title: "Success",
        description: "Preferences updated successfully",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSaveAccount = () => {
    updateUserMutation.mutate({
      firstName,
      lastName,
    });
  };

  const handleSaveNotifications = () => {
    updatePreferencesMutation.mutate({
      emailNotifications: emailNotifications ? 1 : 0,
      applicationNotifications: applicationNotifications ? 1 : 0,
      messageNotifications: messageNotifications ? 1 : 0,
      opportunityNotifications: opportunityNotifications ? 1 : 0,
    });
  };

  const handleSavePrivacy = () => {
    updatePreferencesMutation.mutate({
      profileVisibility,
    });
  };

  const handleProfileImageComplete = async (uploadURL: string) => {
    try {
      const response: any = await apiRequest("PUT", "/api/profile/image", {
        imageURL: uploadURL,
      });
      setProfileImageUrl(response.imagePath);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Success",
        description: "Profile image updated successfully",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="border-b bg-background/95 backdrop-blur sticky top-0 z-40">
        <div className="mx-auto max-w-4xl px-4 py-6 sm:px-6 lg:px-8">
          <div className="flex items-center gap-4">
            <Button variant="ghost" size="icon" onClick={() => setLocation("/")} data-testid="button-back">
              <ArrowLeft className="h-4 w-4" />
            </Button>
            <div>
              <h1 className="font-serif text-3xl font-bold">Settings</h1>
              <p className="text-sm text-muted-foreground">
                Manage your account and preferences
              </p>
            </div>
          </div>
        </div>
      </div>

      <div className="mx-auto max-w-4xl px-4 py-8 sm:px-6 lg:px-8">
        <Tabs defaultValue="account" className="space-y-6">
          <TabsList className="grid w-full grid-cols-3" data-testid="tabs-list">
            <TabsTrigger value="account" data-testid="tab-account">
              <User className="mr-2 h-4 w-4" />
              Account
            </TabsTrigger>
            <TabsTrigger value="notifications" data-testid="tab-notifications">
              <Bell className="mr-2 h-4 w-4" />
              Notifications
            </TabsTrigger>
            <TabsTrigger value="privacy" data-testid="tab-privacy">
              <Lock className="mr-2 h-4 w-4" />
              Privacy
            </TabsTrigger>
          </TabsList>

          <TabsContent value="account" className="space-y-6">
            <Card className="p-6">
              <h2 className="font-serif text-xl font-semibold mb-4">Account Information</h2>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    value={email}
                    disabled
                    className="mt-2"
                    data-testid="input-email"
                  />
                  <p className="text-xs text-muted-foreground mt-1">
                    Email cannot be changed
                  </p>
                </div>

                <div>
                  <Label htmlFor="firstName">First Name</Label>
                  <Input
                    id="firstName"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    className="mt-2"
                    data-testid="input-first-name"
                  />
                </div>

                <div>
                  <Label htmlFor="lastName">Last Name</Label>
                  <Input
                    id="lastName"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    className="mt-2"
                    data-testid="input-last-name"
                  />
                </div>

                <div>
                  <Label>Profile Image</Label>
                  <div className="mt-2 flex items-center gap-4">
                    {profileImageUrl && (
                      <img
                        src={profileImageUrl}
                        alt="Profile"
                        className="h-16 w-16 rounded-full object-cover"
                      />
                    )}
                    <SimpleUploader
                      onUploadComplete={handleProfileImageComplete}
                      accept="image/*"
                      data-testid="button-upload-profile-image"
                    >
                      Change Image
                    </SimpleUploader>
                  </div>
                </div>
              </div>

              <div className="mt-6 flex justify-end">
                <Button
                  onClick={handleSaveAccount}
                  disabled={updateUserMutation.isPending}
                  data-testid="button-save-account"
                >
                  {updateUserMutation.isPending ? "Saving..." : "Save Changes"}
                </Button>
              </div>
            </Card>

            <Card className="p-6">
              <h2 className="font-serif text-xl font-semibold mb-4">Authentication</h2>
              <p className="text-sm text-muted-foreground mb-6">
                Manage your login sessions
              </p>
              <div className="flex items-center gap-4">
                <Button
                  variant="outline"
                  asChild
                  data-testid="button-login"
                >
                  <a href="/api/login">
                    <LogIn className="mr-2 h-4 w-4" />
                    Login Again
                  </a>
                </Button>
                <Button
                  variant="destructive"
                  asChild
                  data-testid="button-logout"
                >
                  <a href="/api/logout">
                    <LogOut className="mr-2 h-4 w-4" />
                    Logout
                  </a>
                </Button>
              </div>
            </Card>
          </TabsContent>

          <TabsContent value="notifications" className="space-y-6">
            <Card className="p-6">
              <h2 className="font-serif text-xl font-semibold mb-4">Notification Preferences</h2>
              <div className="space-y-6">
                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label htmlFor="email-notifications">Email Notifications</Label>
                    <p className="text-sm text-muted-foreground">
                      Receive notifications via email
                    </p>
                  </div>
                  <Switch
                    id="email-notifications"
                    checked={emailNotifications}
                    onCheckedChange={setEmailNotifications}
                    data-testid="switch-email-notifications"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label htmlFor="application-notifications">Application Updates</Label>
                    <p className="text-sm text-muted-foreground">
                      Get notified about application status changes
                    </p>
                  </div>
                  <Switch
                    id="application-notifications"
                    checked={applicationNotifications}
                    onCheckedChange={setApplicationNotifications}
                    data-testid="switch-application-notifications"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label htmlFor="message-notifications">New Messages</Label>
                    <p className="text-sm text-muted-foreground">
                      Get notified when you receive new messages
                    </p>
                  </div>
                  <Switch
                    id="message-notifications"
                    checked={messageNotifications}
                    onCheckedChange={setMessageNotifications}
                    data-testid="switch-message-notifications"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label htmlFor="opportunity-notifications">New Opportunities</Label>
                    <p className="text-sm text-muted-foreground">
                      Get notified about new collaboration opportunities
                    </p>
                  </div>
                  <Switch
                    id="opportunity-notifications"
                    checked={opportunityNotifications}
                    onCheckedChange={setOpportunityNotifications}
                    data-testid="switch-opportunity-notifications"
                  />
                </div>
              </div>

              <div className="mt-6 flex justify-end">
                <Button
                  onClick={handleSaveNotifications}
                  disabled={updatePreferencesMutation.isPending}
                  data-testid="button-save-notifications"
                >
                  {updatePreferencesMutation.isPending ? "Saving..." : "Save Changes"}
                </Button>
              </div>
            </Card>
          </TabsContent>

          <TabsContent value="privacy" className="space-y-6">
            <Card className="p-6">
              <h2 className="font-serif text-xl font-semibold mb-4">Privacy Settings</h2>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="profile-visibility">Profile Visibility</Label>
                  <p className="text-sm text-muted-foreground mt-1 mb-2">
                    Control who can view your profile
                  </p>
                  <Select
                    value={profileVisibility}
                    onValueChange={setProfileVisibility}
                  >
                    <SelectTrigger className="w-full" data-testid="select-profile-visibility">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="public" data-testid="option-public">
                        Public - Anyone can view your profile
                      </SelectItem>
                      <SelectItem value="private" data-testid="option-private">
                        Private - Only you can view your profile
                      </SelectItem>
                      <SelectItem value="connections" data-testid="option-connections">
                        Connections Only - Only people you've connected with
                      </SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="mt-6 flex justify-end">
                <Button
                  onClick={handleSavePrivacy}
                  disabled={updatePreferencesMutation.isPending}
                  data-testid="button-save-privacy"
                >
                  {updatePreferencesMutation.isPending ? "Saving..." : "Save Changes"}
                </Button>
              </div>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/project-detail.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useLocation, useRoute } from "wouter";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { ArrowLeft, User as UserIcon, Music, Video } from "lucide-react";
import { Skeleton } from "@/components/ui/skeleton";
import { ProjectMediaDisplay } from "@/components/ProjectMediaDisplay";
import type { Project, User as UserType } from "@shared/schema";

export default function ProjectDetail() {
  const [, params] = useRoute("/projects/:id");
  const [, setLocation] = useLocation();

  const { data: project, isLoading } = useQuery<
    Project & {
      artist: {
        user: UserType;
        bio: string | null;
        location: string | null;
      };
    }
  >({
    queryKey: ["/api/projects", params?.id],
    enabled: !!params?.id,
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <div className="mx-auto max-w-4xl px-4 py-8 sm:px-6 lg:px-8">
          <Skeleton className="h-12 w-1/2" />
          <Skeleton className="mt-4 h-96 w-full" />
          <Skeleton className="mt-4 h-32 w-full" />
        </div>
      </div>
    );
  }

  if (!project) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <Card className="p-12 text-center">
          <p className="text-muted-foreground">Project not found</p>
          <Button className="mt-4" onClick={() => setLocation("/discover")}>
            Back to Discover
          </Button>
        </Card>
      </div>
    );
  }

  const getUserInitials = () => {
    const user = project.artist.user;
    if (user.firstName && user.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`;
    }
    return user.email?.[0]?.toUpperCase() || "U";
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="border-b bg-background/95 backdrop-blur sticky top-0 z-40">
        <div className="mx-auto max-w-4xl px-4 py-6 sm:px-6 lg:px-8">
          <div className="flex items-center gap-4">
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setLocation("/discover")}
              data-testid="button-back"
            >
              <ArrowLeft className="h-4 w-4" />
            </Button>
            <div className="flex-1 min-w-0">
              <h1 className="font-serif text-2xl font-bold sm:text-3xl line-clamp-1">
                {project.title}
              </h1>
              <p className="text-sm text-muted-foreground">
                by {project.artist.user.firstName || project.artist.user.email}
              </p>
            </div>
            {project.mediaType && (
              <Badge variant="secondary" className="gap-2">
                {project.mediaType === 'audio' && <Music className="h-3 w-3" />}
                {project.mediaType === 'video' && <Video className="h-3 w-3" />}
                {project.mediaType}
              </Badge>
            )}
          </div>
        </div>
      </div>

      <div className="mx-auto max-w-4xl px-4 py-8 sm:px-6 lg:px-8">
        <div className="space-y-6">
          <Card className="overflow-hidden">
            <div className="relative aspect-video bg-muted">
              <ProjectMediaDisplay project={project} showControls={true} />
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-start gap-4">
              <Avatar className="h-12 w-12">
                <AvatarImage src={project.artist.user.profileImageUrl || undefined} />
                <AvatarFallback>{getUserInitials()}</AvatarFallback>
              </Avatar>
              <div className="flex-1 min-w-0">
                <h2 className="font-serif text-xl font-semibold">
                  {project.artist.user.firstName && project.artist.user.lastName
                    ? `${project.artist.user.firstName} ${project.artist.user.lastName}`
                    : project.artist.user.email}
                </h2>
                {project.artist.location && (
                  <p className="text-sm text-muted-foreground">
                    {project.artist.location}
                  </p>
                )}
                {project.artist.bio && (
                  <p className="mt-2 text-sm text-muted-foreground line-clamp-2">
                    {project.artist.bio}
                  </p>
                )}
              </div>
            </div>
          </Card>

          <Card className="p-6">
            <h3 className="font-serif text-xl font-semibold">About This Project</h3>
            <p className="mt-4 whitespace-pre-wrap leading-relaxed text-muted-foreground">
              {project.description}
            </p>
          </Card>

          {project.genres && project.genres.length > 0 && (
            <Card className="p-6">
              <h3 className="font-serif text-xl font-semibold">Genres</h3>
              <div className="mt-4 flex flex-wrap gap-2">
                {project.genres.map((genre, idx) => (
                  <Badge key={idx} variant="outline">
                    {genre}
                  </Badge>
                ))}
              </div>
            </Card>
          )}

          {project.mediaDuration && (
            <Card className="p-6">
              <h3 className="font-serif text-xl font-semibold">Duration</h3>
              <p className="mt-2 text-muted-foreground">
                {Math.floor(project.mediaDuration / 60)}:{String(Math.floor(project.mediaDuration % 60)).padStart(2, '0')}
              </p>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/AppHeader.tsx.content:
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import {
  Music,
  Search,
  MessageSquare,
  Briefcase,
  FileText,
  FolderOpen,
  BarChart3,
  Settings as SettingsIcon,
  LogOut,
  Menu,
  X,
} from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { NotificationBell } from "@/components/NotificationBell";
import { useState } from "react";

export function AppHeader() {
  const { user } = useAuth();
  const [location] = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const getUserInitials = () => {
    if (!user) return "U";
    if (user.firstName && user.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`;
    }
    return user.email?.[0]?.toUpperCase() || "U";
  };

  const navLinks = [
    { href: "/", label: "Home", icon: Music, show: true },
    { href: "/discover", label: "Discover", icon: Search, show: true },
    { href: "/messages", label: "Messages", icon: MessageSquare, show: true },
    { href: "/opportunities/new", label: "Post Opportunity", icon: Briefcase, show: user?.role === "label" },
    { href: "/applications", label: "Applications", icon: FileText, show: user?.role === "label" },
    { href: "/analytics", label: "Analytics", icon: BarChart3, show: user?.role === "label" },
    { href: "/projects/new", label: "Share Project", icon: FolderOpen, show: user?.role === "artist" },
  ].filter(link => link.show);

  return (
    <header className="sticky top-0 z-50 border-b bg-background/95 backdrop-blur">
      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
        <div className="flex h-16 items-center justify-between gap-4">
          <Link href="/" className="flex items-center gap-2">
            <Music className="h-7 w-7 text-primary" />
            <span className="font-serif text-2xl font-bold">COLZ</span>
          </Link>

          {/* Desktop Navigation */}
          <nav className="hidden items-center gap-2 md:flex">
            {navLinks.map((link) => (
              <Link key={link.href} href={link.href}>
                <Button
                  variant={location === link.href ? "secondary" : "ghost"}
                  size="sm"
                  className="gap-2"
                  data-testid={`link-${link.label.toLowerCase().replace(/\s+/g, '-')}`}
                  onClick={() => setMobileMenuOpen(false)}
                >
                  <link.icon className="h-4 w-4" />
                  {link.label}
                </Button>
              </Link>
            ))}
          </nav>

          {/* Desktop Actions */}
          <div className="hidden items-center gap-2 md:flex">
            <NotificationBell />
            <Button
              variant="ghost"
              size="icon"
              asChild
              data-testid="button-settings"
            >
              <Link href="/settings">
                <SettingsIcon className="h-4 w-4" />
              </Link>
            </Button>
            <Button
              variant="ghost"
              size="icon"
              asChild
              data-testid="button-profile"
            >
              <Link href="/profile">
                <Avatar className="h-8 w-8">
                  <AvatarImage src={user?.profileImageUrl || undefined} />
                  <AvatarFallback>{getUserInitials()}</AvatarFallback>
                </Avatar>
              </Link>
            </Button>
          </div>

          {/* Mobile Menu Button */}
          <div className="flex items-center gap-2 md:hidden">
            <NotificationBell />
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              data-testid="button-mobile-menu"
            >
              {mobileMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
            </Button>
          </div>
        </div>

        {/* Mobile Menu */}
        {mobileMenuOpen && (
          <div className="border-t py-4 md:hidden">
            <nav className="space-y-2">
              {navLinks.map((link) => (
                <Link key={link.href} href={link.href}>
                  <Button
                    variant={location === link.href ? "secondary" : "ghost"}
                    className="w-full justify-start gap-2"
                    onClick={() => setMobileMenuOpen(false)}
                    data-testid={`link-mobile-${link.label.toLowerCase().replace(/\s+/g, '-')}`}
                  >
                    <link.icon className="h-4 w-4" />
                    {link.label}
                  </Button>
                </Link>
              ))}
              <Link href="/settings">
                <Button
                  variant="ghost"
                  className="w-full justify-start gap-2"
                  onClick={() => setMobileMenuOpen(false)}
                  data-testid="link-mobile-settings"
                >
                  <SettingsIcon className="h-4 w-4" />
                  Settings
                </Button>
              </Link>
              <Link href="/profile">
                <Button
                  variant="ghost"
                  className="w-full justify-start gap-2"
                  onClick={() => setMobileMenuOpen(false)}
                  data-testid="link-mobile-profile"
                >
                  <Avatar className="h-4 w-4">
                    <AvatarImage src={user?.profileImageUrl || undefined} />
                    <AvatarFallback className="text-xs">{getUserInitials()}</AvatarFallback>
                  </Avatar>
                  Profile
                </Button>
              </Link>
            </nav>
          </div>
        )}
      </div>
    </header>
  );
}

client/src/pages/signup.tsx.content:
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Music, Building2, CheckCircle2 } from "lucide-react";
import { useState } from "react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";

export default function Signup() {
  const [step, setStep] = useState<"info" | "role">("info");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [selectedRole, setSelectedRole] = useState<"artist" | "label" | null>(null);
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const updateUserMutation = useMutation({
    mutationFn: async (data: { firstName: string; lastName: string }) => {
      const response = await apiRequest("PATCH", "/api/auth/user", data);
      return await response.json();
    },
    onSuccess: () => {
      setStep("role");
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const selectRoleMutation = useMutation({
    mutationFn: async (role: "artist" | "label") => {
      const response = await apiRequest("POST", "/api/users/role", { role });
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      setLocation("/");
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleInfoSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (firstName.trim() && lastName.trim()) {
      updateUserMutation.mutate({ firstName, lastName });
    }
  };

  const handleRoleSubmit = () => {
    if (selectedRole) {
      selectRoleMutation.mutate(selectedRole);
    }
  };

  if (step === "info") {
    return (
      <div className="flex min-h-screen flex-col items-center justify-center bg-background px-4 py-12">
        <div className="w-full max-w-md">
          <div className="text-center mb-8">
            <div className="flex items-center justify-center gap-2 mb-4">
              <Music className="h-10 w-10 text-primary" />
              <span className="font-serif text-3xl font-bold">COLZ</span>
            </div>
            <h1 className="font-serif text-3xl font-bold sm:text-4xl">
              Welcome to COLZ
            </h1>
            <p className="mt-2 text-lg text-muted-foreground">
              Let's get to know you
            </p>
          </div>

          <Card className="p-8">
            <form onSubmit={handleInfoSubmit} className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="firstName">First Name *</Label>
                <Input
                  id="firstName"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                  placeholder="Enter your first name"
                  required
                  data-testid="input-first-name"
                  autoFocus
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="lastName">Last Name *</Label>
                <Input
                  id="lastName"
                  value={lastName}
                  onChange={(e) => setLastName(e.target.value)}
                  placeholder="Enter your last name"
                  required
                  data-testid="input-last-name"
                />
              </div>

              <Button
                type="submit"
                className="w-full"
                size="lg"
                disabled={!firstName.trim() || !lastName.trim() || updateUserMutation.isPending}
                data-testid="button-continue-info"
              >
                {updateUserMutation.isPending ? "Saving..." : "Continue"}
              </Button>
            </form>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen flex-col items-center justify-center bg-background px-4 py-12">
      <div className="w-full max-w-4xl">
        <div className="text-center">
          <h1 className="font-serif text-3xl font-bold sm:text-4xl">
            Welcome, {firstName}!
          </h1>
          <p className="mt-2 text-lg text-muted-foreground">
            Choose your role to get started
          </p>
        </div>

        <div className="mt-12 grid gap-6 sm:grid-cols-2">
          <Card
            className={`cursor-pointer p-8 transition-all hover-elevate ${
              selectedRole === "artist" ? "ring-2 ring-primary" : ""
            }`}
            onClick={() => setSelectedRole("artist")}
            data-testid="card-role-artist"
          >
            <div className="flex items-start justify-between">
              <div className="flex h-12 w-12 items-center justify-center rounded-md bg-primary/10">
                <Music className="h-6 w-6 text-primary" />
              </div>
              {selectedRole === "artist" && (
                <CheckCircle2 className="h-6 w-6 text-primary" data-testid="icon-selected-artist" />
              )}
            </div>
            <h2 className="mt-4 font-serif text-2xl font-semibold">
              I'm an Artist
            </h2>
            <p className="mt-2 leading-relaxed text-muted-foreground">
              Showcase your portfolio, find collaboration opportunities, 
              and connect with labels and other artists.
            </p>
            <ul className="mt-6 space-y-2 text-sm">
              <li className="flex items-start gap-2">
                <CheckCircle2 className="mt-0.5 h-4 w-4 flex-shrink-0 text-primary" />
                <span>Create a stunning artist profile</span>
              </li>
              <li className="flex items-start gap-2">
                <CheckCircle2 className="mt-0.5 h-4 w-4 flex-shrink-0 text-primary" />
                <span>Upload portfolio samples</span>
              </li>
              <li className="flex items-start gap-2">
                <CheckCircle2 className="mt-0.5 h-4 w-4 flex-shrink-0 text-primary" />
                <span>Apply to opportunities</span>
              </li>
              <li className="flex items-start gap-2">
                <CheckCircle2 className="mt-0.5 h-4 w-4 flex-shrink-0 text-primary" />
                <span>Share your projects</span>
              </li>
            </ul>
          </Card>

          <Card
            className={`cursor-pointer p-8 transition-all hover-elevate ${
              selectedRole === "label" ? "ring-2 ring-primary" : ""
            }`}
            onClick={() => setSelectedRole("label")}
            data-testid="card-role-label"
          >
            <div className="flex items-start justify-between">
              <div className="flex h-12 w-12 items-center justify-center rounded-md bg-primary/10">
                <Building2 className="h-6 w-6 text-primary" />
              </div>
              {selectedRole === "label" && (
                <CheckCircle2 className="h-6 w-6 text-primary" data-testid="icon-selected-label" />
              )}
            </div>
            <h2 className="mt-4 font-serif text-2xl font-semibold">
              I'm a Label
            </h2>
            <p className="mt-2 leading-relaxed text-muted-foreground">
              Post opportunities, discover talented artists, 
              and manage applications all in one place.
            </p>
            <ul className="mt-6 space-y-2 text-sm">
              <li className="flex items-start gap-2">
                <CheckCircle2 className="mt-0.5 h-4 w-4 flex-shrink-0 text-primary" />
                <span>Create a professional label profile</span>
              </li>
              <li className="flex items-start gap-2">
                <CheckCircle2 className="mt-0.5 h-4 w-4 flex-shrink-0 text-primary" />
                <span>Post collaboration opportunities</span>
              </li>
              <li className="flex items-start gap-2">
                <CheckCircle2 className="mt-0.5 h-4 w-4 flex-shrink-0 text-primary" />
                <span>Browse artist profiles</span>
              </li>
              <li className="flex items-start gap-2">
                <CheckCircle2 className="mt-0.5 h-4 w-4 flex-shrink-0 text-primary" />
                <span>Manage applications</span>
              </li>
            </ul>
          </Card>
        </div>

        <div className="mt-8 flex justify-center gap-4">
          <Button
            variant="outline"
            size="lg"
            onClick={() => setStep("info")}
            data-testid="button-back"
          >
            Back
          </Button>
          <Button
            size="lg"
            disabled={!selectedRole || selectRoleMutation.isPending}
            onClick={handleRoleSubmit}
            data-testid="button-continue-role"
          >
            {selectRoleMutation.isPending ? "Setting up..." : "Get Started"}
          </Button>
        </div>
      </div>
    </div>
  );
}

